import React, { useState, useEffect } from 'react';
import { LoadScript, Autocomplete } from '@react-google-maps/api';
import api from '../services/api';
import { deleteTrip } from '../services/api';
import { useHistory } from 'react-router-dom';
import MapComponent from './MapComponent';

function TripManager() {
  const [trips, setTrips] = useState([]);
  const [tripName, setTripName] = useState('');
  const [destination, setDestination] = useState('');
  const [latitude, setLatitude] = useState(null);
  const [longitude, setLongitude] = useState(null);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [budget, setBudget] = useState('');
  const [activities, setActivities] = useState({});
  const [showActivities, setShowActivities] = useState({});
  const [autocomplete, setAutocomplete] = useState(null);
  const [user, setUser] = useState(null);
  const history = useHistory();

  const userId = localStorage.getItem('user_id');

  useEffect(() => {
    if (!userId) {
      alert("Please log in to view your trips");
      history.push('/login');
    } else {
      fetchUserData();
      fetchUserTrips();
    }
  }, [userId, history]);

  const fetchUserData = async () => {
    try {
      const response = await api.get(`/profile/${userId}`);
      setUser(response.data);
    } catch (error) {
      console.error("Error fetching user data:", error);
      alert('Failed to fetch user profile.');
    }
  };

  const fetchUserTrips = async () => {
    try {
      const ownedTripsResponse = await api.get(`/trips?user_id=${userId}`);
      const ownedTrips = ownedTripsResponse.data.map(trip => ({
        ...trip,
        sharedWith: trip.sharedWith || [],
      }));

      const sharedTripsResponse = await api.get(`/users/${userId}/shared-trips`);
      const sharedTrips = sharedTripsResponse.data.map(trip => ({
        ...trip,
        shared: true,
      }));

      const allTrips = [...ownedTrips, ...sharedTrips];
      setTrips(allTrips);
    } catch (error) {
      console.error("Error fetching trips:", error);
      alert('Failed to load trips.');
    }
  };

  const fetchActivities = async (tripId) => {
    try {
      const response = await api.get(`/trips/${tripId}/activities`);
      setActivities(prev => ({
        ...prev,
        [tripId]: response.data.length
          ? response.data
          : 'No activities for this trip.',
      }));
    } catch (error) {
      console.error(`Error fetching activities for trip ${tripId}:`, error);
      setActivities(prev => ({
        ...prev,
        [tripId]: 'No activities for this trip.',
      }));
    }
  };

  const toggleActivities = (tripId) => {
    setShowActivities(prev => ({ ...prev, [tripId]: !prev[tripId] }));
    if (!showActivities[tripId]) {
      fetchActivities(tripId);
    }
  };

  const createTrip = async () => {
    if (budget < 0) {
      alert("Budget must be a positive number");
      return;
    }

    try {
      await api.post('/trips', {
        name: tripName,
        destination,
        latitude,
        longitude,
        start_date: startDate,
        end_date: endDate,
        user_id: userId,
        budget: parseFloat(budget) || 0.0
      });
      fetchUserTrips();
      alert('Trip created successfully!');
    } catch (error) {
      alert('Failed to create trip.');
    }
  };

  const handleDeleteTrip = async (tripId) => {
    if (window.confirm("Are you sure you want to delete this trip?")) {
      try {
        await deleteTrip(tripId);
        alert("Trip deleted successfully");
        fetchUserTrips();
      } catch (error) {
        alert("Failed to delete trip");
      }
    }
  };

  const handlePrint = async () => {
    if (!user) {
      alert("User data is not available.");
      return;
    }

    const activitiesMap = {};
    for (const trip of trips) {
      try {
        const response = await api.get(`/trips/${trip.id}/activities`);
        activitiesMap[trip.id] = response.data.length
          ? response.data
          : 'No activities for this trip.';
      } catch (error) {
        console.error(`Error fetching activities for trip ${trip.id}:`, error);
        activitiesMap[trip.id] = 'No activities for this trip.';
      }
    }

    const tripData = {
      user,
      trips,
      activities: activitiesMap,
    };

    history.push({
      pathname: '/print',
      state: tripData,
    });
  };

  const formatDate = (dateString) => {
    const [year, month, day] = dateString.split('-');
    const date = new Date(year, month - 1, day);
    return date.toLocaleDateString('en-US', { month: 'long', day: 'numeric', year: 'numeric' });
  };

  const onLoad = (autocompleteInstance) => {
    setAutocomplete(autocompleteInstance);
  };

  const onPlaceChanged = () => {
    if (autocomplete) {
      const place = autocomplete.getPlace();
      if (place.geometry) {
        setDestination(place.formatted_address);
        setLatitude(place.geometry.location.lat());
        setLongitude(place.geometry.location.lng());
      } else {
        alert("Please select a valid location from the suggestions.");
      }
    }
  };

  return (
    <LoadScript googleMapsApiKey="YOUR_GOOGLE_MAPS_API_KEY" libraries={['places']}>
      <div style={{ display: 'flex' }}>
        <div style={{ flex: 1 }}>
          <h1>Manage Trips</h1>
          <button onClick={() => {
            localStorage.removeItem('user_id');
            history.push('/login');
          }}>Logout</button>
          <div>
            <input
              type="text"
              placeholder="Trip Name"
              onChange={(e) => setTripName(e.target.value)}
            />
            <Autocomplete onLoad={onLoad} onPlaceChanged={onPlaceChanged}>
              <input
                type="text"
                placeholder="Search Destination"
                style={{ width: '100%', padding: '8px' }}
              />
            </Autocomplete>
            <input
              type="date"
              onChange={(e) => setStartDate(e.target.value)}
            />
            <input
              type="date"
              onChange={(e) => setEndDate(e.target.value)}
            />
            <input
              type="number"
              placeholder="Budget"
              min="0"
              step="0.01"
              onChange={(e) => setBudget(e.target.value)}
            />
            <button onClick={createTrip}>Create Trip</button>
          </div>
          <h2>Your Trips</h2>
          {trips.length > 0 ? (
            <ul style={{ listStyleType: 'none', padding: 0 }}>
              {trips.map(trip => (
                <li key={trip.id} style={{ marginBottom: '20px' }}>
                  <h3>
                    {trip.name} - {trip.destination} (
                    {formatDate(trip.start_date)} - {formatDate(trip.end_date)})
                  </h3>
                  <p>Budget: ${trip.budget}</p>
                  <button onClick={() => toggleActivities(trip.id)}>
                    {showActivities[trip.id] ? 'Hide Activities' : 'View Activities'}
                  </button>
                  <button onClick={() => handleDeleteTrip(trip.id)}>Delete Trip</button>
                  {showActivities[trip.id] && activities[trip.id] && (
                    <ul>
                      {Array.isArray(activities[trip.id]) ? (
                        activities[trip.id].map(activity => (
                          <li key={activity.id}>
                            {activity.name} - {activity.date} at {activity.time}, {activity.location}
                          </li>
                        ))
                      ) : (
                        <p>{activities[trip.id]}</p>
                      )}
                    </ul>
                  )}
                </li>
              ))}
            </ul>
          ) : (
            <p>No trips found. Create a new trip to get started!</p>
          )}
        </div>

        <div style={{ width: '30%', marginLeft: '20px' }}>
          <h3>Map</h3>
          <MapComponent latitude={latitude} longitude={longitude} width="100%" height="200px" />
        </div>

        <div style={{ marginTop: '20px' }}>
          <button onClick={handlePrint}>Print</button>
        </div>
      </div>
    </LoadScript>
  );
}

export default TripManager;
