// ItineraryManager.jsx
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function ItineraryManager() {
  const [activities, setActivities] = useState([]);
  const [newActivity, setNewActivity] = useState({
    name: '',
    date: '',
    time: '',
    location: '',
  });

  const { tripId } = useParams(); // Get trip ID from route parameters

  useEffect(() => {
    // Fetch activities for the current trip
    fetch(`/trips/${tripId}/activities`)
      .then((response) => response.json())
      .then((data) => setActivities(data))
      .catch((error) => console.error('Error fetching activities:', error));
  }, [tripId]);

  const handleChange = (e) => {
    setNewActivity({
      ...newActivity,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // POST request to add a new activity
    fetch(`/trips/${tripId}/activities`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(newActivity),
    })
      .then((response) => response.json())
      .then((data) => {
        // Refresh the activities list after adding
        setActivities([...activities, data]);
        setNewActivity({ name: '', date: '', time: '', location: '' }); // Reset the form
      })
      .catch((error) => console.error('Error adding activity:', error));
  };


  return (
    <div>
      <h1>Itinerary for Trip {tripId}</h1>
      <h2>Manage Itinerary for Trip {tripId}</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Activity Name"
          value={newActivity.name}
          onChange={handleChange}
          required
        />
        <input
          type="date"
          name="date"
          value={newActivity.date}
          onChange={handleChange}
          required
        />
        <input
          type="time"
          name="time"
          value={newActivity.time}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="location"
          placeholder="Location"
          value={newActivity.location}
          onChange={handleChange}
          required
        />
        <button type="submit">Add Activity</button>
      </form>

      <h3>Activities:</h3>
      <ul>
        {activities.map((activity) => (
          <li key={activity.id}>
            {activity.name} - {activity.date} at {activity.time} ({activity.location})
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ItineraryManager;
