import React, { useState, useEffect } from 'react';

const ItineraryManager = () => {
  // State to store trips, activities, selected trip, and new activity input
  const [trips, setTrips] = useState([]);
  const [selectedTrip, setSelectedTrip] = useState('');
  const [activities, setActivities] = useState([]);
  const [activity, setActivity] = useState({
    name: '',
    date: '',
    time: '',
    location: '',
  });

  // Fetch trips from the server
  useEffect(() => {
    const fetchTrips = async () => {
      try {
        const response = await fetch('/api/trips?user_id=1'); // Replace with dynamic user ID as needed
        if (response.ok) {
          const data = await response.json();
          setTrips(data);
        } else {
          console.error('Failed to fetch trips');
        }
      } catch (error) {
        console.error('Error fetching trips:', error);
      }
      console.log("Fetched trips:", data);
    };

    fetchTrips();
  }, []);

  // Fetch activities whenever a trip is selected
  useEffect(() => {
    if (selectedTrip) {
      const fetchActivities = async () => {
        try {
          const response = await fetch(`/api/trips/${selectedTrip}/itinerary/activities`);
          if (response.ok) {
            const data = await response.json();
            setActivities(data);
          } else {
            console.error('Failed to fetch activities');
          }
        } catch (error) {
          console.error('Error fetching activities:', error);
        }
      };

      fetchActivities();
    }
  }, [selectedTrip]);

  // Handle input changes for activity fields
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setActivity((prevActivity) => ({
      ...prevActivity,
      [name]: value,
    }));
  };

  // Handle trip selection
  const handleTripSelection = (e) => {
    setSelectedTrip(e.target.value);
  };

  // Add a new activity to the selected trip
  const addActivity = async (e) => {
    e.preventDefault();
    if (activity.name && activity.date && activity.time && activity.location && selectedTrip) {
      try {
        const response = await fetch(`/api/trips/${selectedTrip}/itinerary/activities`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            name: activity.name,
            date: activity.date,
            time: activity.time,
            location: activity.location,
          }),
        });

        if (response.ok) {
          const newActivity = await response.json();
          setActivities((prevActivities) => [...prevActivities, newActivity]);
          // Reset form fields after adding
          setActivity({ name: '', date: '', time: '', location: '' });
        } else {
          console.error('Failed to add activity');
        }
      } catch (error) {
        console.error('Error adding activity:', error);
      }
    } else {
      alert('Please fill out all fields and select a trip');
    }
  };

  return (
    <div className="itinerary-manager">
      <h2>Create Itinerary</h2>

      {/* Form to add a new activity */}
      <form onSubmit={addActivity}>
        <div>
          <label>Trip:</label>
          <select value={selectedTrip} onChange={handleTripSelection}>
            <option value="">Select a Trip</option>
            {trips.map((trip) => (
              <option key={trip.id} value={trip.id}>{trip.name}</option>
            ))}
          </select>
        </div>
        <div>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={activity.name}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Date:</label>
          <input
            type="date"
            name="date"
            value={activity.date}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Time:</label>
          <input
            type="time"
            name="time"
            value={activity.time}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Location:</label>
          <input
            type="text"
            name="location"
            value={activity.location}
            onChange={handleInputChange}
          />
        </div>
        <button type="submit">Add Activity</button>
      </form>

      {/* Display the list of activities grouped by trip */}
      <h3>Planned Activities</h3>
      {selectedTrip ? (
        <ul>
          {activities.map((activity) => (
            <li key={activity.id}>
              <strong>{activity.name}</strong> - {activity.date} at {activity.time}, {activity.location}
            </li>
          ))}
        </ul>
      ) : (
        <p>Select a trip to view activities.</p>
      )}
    </div>
  );
};

export default ItineraryManager;
