import React, { useState } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

const MapComponent = ({ width = '100%', height = '300px' }) => {
  const containerStyle = {
    width: width,
    height: height,
  };

  const center = {
    lat: 39.8283, // Default center (USA)
    lng: -98.5795,
  };

  const [mapCenter, setMapCenter] = useState(center);
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearch = () => {
    fetch(`https://maps.googleapis.com/maps/api/geocode/json?address=${encodeURIComponent(searchTerm)}&key=YOUR_GOOGLE_MAPS_API_KEY`)
      .then(response => response.json())
      .then(data => {
        if (data.results && data.results[0]) {
          const location = data.results[0].geometry.location;
          setMapCenter({ lat: location.lat, lng: location.lng });
        } else {
          alert("Location not found");
        }
      })
      .catch(error => console.error("Error fetching location data:", error));
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Search location"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>

      <LoadScript googleMapsApiKey="AIzaSyCbJ9OEK1bSd7DLg2XHOE8zT2PlBxODR1g">
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={mapCenter}
          zoom={5}
        >
          <Marker position={mapCenter} />
        </GoogleMap>
      </LoadScript>
    </div>
  );
};

export default MapComponent;
