import React, { useState, useEffect } from 'react';
import api, { deleteActivity } from '../services/api';  // Import deleteActivity API function

function ItineraryManager({ tripId }) {
  const [activities, setActivities] = useState([]);

  useEffect(() => {
    fetchActivities();
  }, []);

  const fetchActivities = async () => {
    try {
      const response = await api.get(`/trips/${tripId}/itinerary/activities`);
      setActivities(response.data);
    } catch (error) {
      console.error('Failed to load activities:', error);
    }
  };

  const handleDeleteActivity = async (activityId) => {
    if (window.confirm("Are you sure you want to delete this activity?")) {
      try {
        await deleteActivity(tripId, activityId);
        alert("Activity deleted successfully");
        fetchActivities();  // Refresh list after deletion
      } catch (error) {
        alert("Failed to delete activity");
      }
    }
  };

  return (
    <div>
      <h3>Itinerary</h3>
      {activities.length > 0 ? (
        <ul>
          {activities.map(activity => (
            <li key={activity.id}>
              <strong>{activity.name}</strong> - {activity.date} at {activity.time}, {activity.location}
              <button onClick={() => handleDeleteActivity(activity.id)}>Delete</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>No activities found. Add an activity to your itinerary!</p>
      )}
    </div>
  );
}

export default ItineraryManager;
