/*
Author: Kaitlyn Clements
Date: 10/21/2024
Other Sources: Chat GPT
Description: Handles user registration
*/

import React, { useState } from 'react';
import api from '../services/api';  // Import the API instance
import { useHistory } from 'react-router-dom';

function Register() {
  const [firstName, setFirstName] = useState('');  // First name state
  const [lastName, setLastName] = useState('');    // Last name state
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const history = useHistory();

  const handleRegister = async () => {
    try {
      console.log("Attempting to register:", { firstName, lastName, email, password });
      const response = await api.post('/register', {
        "first name": firstName,
        "last name": lastName,
        email,
        password,
      });
      alert('Registration successful!');
      history.push('/login');  // Redirect to login page after registration
    } catch (error) {
      console.error("Registration error:", error);

      if (error.response) {
        console.error("Error response:", error.response);
        alert(`Registration failed: ${error.response.data.error || 'Unknown error'}`);
      } else {
        console.error("Error details:", error.toJSON());  // Show more error details
        alert('Registration failed due to a network error.');
      }
    }
  };



  return (
    <div>
      <h2>Register</h2>
      <input type="text" placeholder="First Name" onChange={(e) => setFirstName(e.target.value)} />
      <p></p>
      <input type="text" placeholder="Last Name" onChange={(e) => setLastName(e.target.value)} />
      <p></p> 
      <input type="email" placeholder="Email" onChange={(e) => setEmail(e.target.value)} />
      <p></p>
      <input type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} />
      <p></p>
      <button onClick={handleRegister}>Register</button>
    </div>
  );
}

export default Register;
