import React, { useEffect, useState } from 'react';
import api from '../services/api';

function BudgetManager() {
  const [trips, setTrips] = useState([]); // State to store trips
  const [selectedTrip, setSelectedTrip] = useState(''); // State to store selected trip ID
  const [activities, setActivities] = useState([]); // State to store activities for the selected trip
  const [tripDetails, setTripDetails] = useState(null); // State to store details of selected trip

  // Get user ID from localStorage
  const userId = localStorage.getItem('user_id');

  useEffect(() => {
    // Function to fetch trips from the backend
    const fetchTrips = async () => {
      try {
        const response = await api.get(`/trips?user_id=${userId}`);
        setTrips(response.data); // Store trips in state
      } catch (error) {
        console.error("Error fetching trips:", error);
        alert("Failed to load trips.");
      }
    };

    // Fetch trips when the component mounts
    if (userId) {
      fetchTrips();
    }
  }, [userId]);

  // Function to fetch activities and trip details for the selected trip
  const fetchTripDetails = async (tripId) => {
    try {
      const response = await api.get(`/trips/${tripId}`);
      setTripDetails(response.data); // Store trip details in state

      const activitiesResponse = await api.get(`/trips/${tripId}/activities`);
      setActivities(activitiesResponse.data); // Store activities in state
    } catch (error) {
      console.error("Error fetching trip details or activities:", error);
      alert("Failed to load trip details.");
    }
  };

  // Handle dropdown selection change
  const handleTripChange = (e) => {
    const tripId = e.target.value;
    setSelectedTrip(tripId);

    if (tripId) {
      fetchTripDetails(tripId); // Fetch details and activities for the selected trip
    } else {
      setTripDetails(null);
      setActivities([]);
    }
  };

  return (
    <div>
      <h1>Budget Manager</h1>

      {/* Dropdown menu for selecting a trip */}
      <label htmlFor="tripDropdown">Select a Trip:</label>
      <select id="tripDropdown" value={selectedTrip} onChange={handleTripChange}>
        <option value="">-- Select a Trip --</option>
        {trips.map((trip) => (
          <option key={trip.id} value={trip.id}>
            {trip.name} - {trip.destination} 
          </option>
        ))}
      </select>

      {/* Display selected trip's details and activities */}
      {tripDetails && (
        <div>
          <h2>Selected Trip Details</h2>
          <p><strong>Destination:</strong> {tripDetails.destination}</p>
          <p><strong>Start Date:</strong> {tripDetails.start_date}</p>
          <p><strong>End Date:</strong> {tripDetails.end_date}</p>
          <p><strong>Budget:</strong> ${tripDetails.budget}</p>

          <h3>Activities</h3>
          {activities.length > 0 ? (
            <ul>
              {activities.map((activity) => (
                <li key={activity.id}>
                  <strong>{activity.name}</strong> - {activity.date} at {activity.time}, {activity.location}
                </li>
              ))}
            </ul>
          ) : (
            <p>No activities for this trip.</p>
          )}
        </div>
      )}
    </div>
  );
}

export default BudgetManager;
