import React, { useState } from 'react';

const ItineraryManager = () => {
  // State to store activities
  const [activities, setActivities] = useState([]);
  const [activity, setActivity] = useState({
    name: '',
    date: '',
    time: '',
    location: '',
  });

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setActivity((prevActivity) => ({
      ...prevActivity,
      [name]: value,
    }));
  };

  // Add new activity to the list
  const addActivity = (e) => {
    e.preventDefault();
    if (activity.name && activity.date && activity.time && activity.location) {
      setActivities((prevActivities) => [...prevActivities, activity]);
      // Reset form fields after adding
      setActivity({ name: '', date: '', time: '', location: '' });
    } else {
      alert('Please fill out all fields');
    }
  };

  return (
    <div className="itinerary-manager">
      <h2>Create Itinerary</h2>
      
      {/* Form to add a new activity */}
      <form onSubmit={addActivity}>
        <div>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={activity.name}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Date:</label>
          <input
            type="date"
            name="date"
            value={activity.date}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Time:</label>
          <input
            type="time"
            name="time"
            value={activity.time}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Location:</label>
          <input
            type="text"
            name="location"
            value={activity.location}
            onChange={handleInputChange}
          />
        </div>
        <button type="submit">Add Activity</button>
      </form>

      {/* Display the list of activities */}
      <h3>Planned Activities</h3>
      {activities.length > 0 ? (
        <ul>
          {activities.map((activity, index) => (
            <li key={index}>
              <strong>{activity.name}</strong> - {activity.date} at {activity.time}, {activity.location}
            </li>
          ))}
        </ul>
      ) : (
        <p>No activities added yet.</p>
      )}
    </div>
  );
};

export default ItineraryManager;
