import React, { useState, useEffect } from 'react';
import api from '../services/api';
import { deleteTrip } from '../services/api';
import { useHistory } from 'react-router-dom';
import MapComponent from './MapComponent'; // Import the Map component

// Modal for editing activities
function EditActivityModal({ show, activity, onClose, onSave }) {
  const [name, setName] = useState(activity?.name || '');
  const [time, setTime] = useState(activity?.time || '');
  const [location, setLocation] = useState(activity?.location || '');

  useEffect(() => {
    if (activity) {
      setName(activity.name);
      setTime(activity.time);
      setLocation(activity.location);
    }
  }, [activity]);

  if (!show) return null;

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave({ id: activity.id, tripId: activity.tripId, name, time, location });
  };

  return (
    <div className="modal">
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </label>
        <label>
          Time:
          <input
            type="time"
            value={time}
            onChange={(e) => setTime(e.target.value)}
          />
        </label>
        <label>
          Location:
          <input
            type="text"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
          />
        </label>
        <button type="submit">Save</button>
        <button type="button" onClick={onClose}>
          Cancel
        </button>
      </form>
    </div>
  );
}

function TripManager() {
  const [trips, setTrips] = useState([]);
  const [tripName, setTripName] = useState('');
  const [destination, setDestination] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [budget, setBudget] = useState(''); // New state for budget
  const [activities, setActivities] = useState({});
  const [showActivities, setShowActivities] = useState({});
  const history = useHistory();
  const [selectedActivity, setSelectedActivity] = useState(null);
  const [isEditing, setIsEditing] = useState(false);

  const userId = localStorage.getItem('user_id');

  useEffect(() => {
    if (!userId) {
      alert("Please log in to view your trips");
      history.push('/login');
    } else {
      fetchUserTrips();
    }
  }, [userId, history]);

  const fetchUserTrips = async () => {
    try {
      const response = await api.get(`/trips?user_id=${userId}`);
      console.log("Trips fetched:", response.data);
      setTrips(response.data);
    } catch (error) {
      alert('Failed to load trips.');
    }
  };

  const fetchActivities = async (tripId) => {
    try {
      const response = await api.get(`/trips/${tripId}/activities`);
      if (response.data.length === 0) {
        setActivities(prev => ({ ...prev, [tripId]: 'No activities created for this trip yet' }));
      } else {
        setActivities(prev => ({ ...prev, [tripId]: response.data }));
      }
    } catch (error) {
      if (error.response && error.response.status === 404) {
        setActivities(prev => ({ ...prev, [tripId]: 'No activities created for this trip yet' }));
      } else {
        console.error("Error fetching activities:", error);
        alert('Failed to load activities for this trip.');
      }
    }
  };

  const toggleActivities = (tripId) => {
    setShowActivities(prev => ({ ...prev, [tripId]: !prev[tripId] }));
    if (!showActivities[tripId]) {
      fetchActivities(tripId);
    }
  };

  const createTrip = async () => {
    if (budget < 0) {
      alert("Budget must be a positive number");
      return;
    }

    try {
      await api.post('/trips', {
        name: tripName,
        destination,
        start_date: startDate,
        end_date: endDate,
        user_id: userId,
        budget: parseFloat(budget) || 0.0  // Include budget here, ensure it's a number
      });
      fetchUserTrips();
      alert('Trip created successfully!');
    } catch (error) {
      alert('Failed to create trip.');
    }
  };

  const handleDeleteTrip = async (tripId) => {
    if (window.confirm("Are you sure you want to delete this trip?")) {
      try {
        await deleteTrip(tripId);
        alert("Trip deleted successfully");
        fetchUserTrips();
      } catch (error) {
        alert("Failed to delete trip");
      }
    }
  };

  const handleDeleteActivity = async (tripId, activityId) => {
    if (window.confirm("Are you sure you want to delete this activity?")) {
      try {
        await api.delete(`/trips/${tripId}/itinerary/activities/${activityId}`);
        alert("Activity deleted successfully");
        fetchActivities(tripId);
      } catch (error) {
        alert("Failed to delete activity");
      }
    }
  };

  const formatDate = (dateString) => {
    const [year, month, day] = dateString.split('-');
    const date = new Date(year, month - 1, day);
    const options = { month: 'long', day: 'numeric' };
    return date.toLocaleDateString('en-US', options);

  };

  const handleEditClick = (activity) => {
    setSelectedActivity(activity);
    setIsEditing(true);
  };

  const handleSave = async (updatedActivity) => {
    try {
      await api.put(
        `/trips/${updatedActivity.tripId}/itinerary/activities/${updatedActivity.id}`,
        {
          name: updatedActivity.name,
          time: updatedActivity.time,
          location: updatedActivity.location,
        }
      );
      alert('Activity updated successfully!');
      setIsEditing(false);
      fetchActivities(updatedActivity.tripId);
    } catch (error) {
      alert('Failed to update activity.');
    }
  };

  return (
    <div style={{ display: 'flex' }}>
      <div style={{ flex: 1 }}>
        <h1>Manage Trips</h1>
        <button
          onClick={() => {
            localStorage.removeItem('user_id');
            history.push('/login');
          }}
        >
          Logout
        </button>
        <div>
          <input
            type="text"
            placeholder="Trip Name"
            onChange={(e) => setTripName(e.target.value)}
          />
          <input
            type="text"
            placeholder="Destination"
            onChange={(e) => setDestination(e.target.value)}
          />
          <input type="date" onChange={(e) => setStartDate(e.target.value)} />
          <input type="date" onChange={(e) => setEndDate(e.target.value)} />
          <input
            type="number"
            placeholder="Budget"
            min="0"
            step="0.01"
            onChange={(e) => setBudget(e.target.value)}
          />
          <button onClick={() => {/* Trip creation logic */}}>Create Trip</button>
        </div>
        <h2>Your Trips</h2>
        {trips.length > 0 ? (
          <ul style={{ listStyleType: 'none', padding: 0 }}>
            {trips.map((trip) => (
              <li key={trip.id} style={{ marginBottom: '20px' }}>
                <div>
                  <h3>
                    <strong>{trip.name}</strong> - {trip.destination} (
                    {trip.start_date} - {trip.end_date})
                  </h3>
                  <button onClick={() => toggleActivities(trip.id)}>
                    {showActivities[trip.id] ? 'Hide Activities' : 'View Activities'}
                  </button>
                </div>
                {showActivities[trip.id] && activities[trip.id] && (
                  <ul>
                    {Array.isArray(activities[trip.id]) ? (
                      activities[trip.id].map((activity) => (
                        <div
                          key={activity.id}
                          className="activity-container"
                          style={{
                            border: '1px solid #ccc',
                            padding: '10px',
                            borderRadius: '8px',
                            marginBottom: '10px',
                          }}
                        >
                          <div className="activity-details">
                            <p>{activity.name}</p>
                            <p>{activity.time}</p>
                            <p>{activity.location}</p>
                            <button onClick={() => handleEditClick({ ...activity, tripId: trip.id })}>
                              Edit
                            </button>
                          </div>
                          {selectedActivity?.id === activity.id && isEditing && (
                            <div className="edit-form" style={{ marginTop: '10px' }}>
                              <form onSubmit={(e) => handleSave(e, activity.id)}>
                                <label>
                                  Name:
                                  <input
                                    type="text"
                                    value={editName}
                                    onChange={(e) => setEditName(e.target.value)}
                                  />
                                </label>
                                <br />
                                <label>
                                  Time:
                                  <input
                                    type="time"
                                    value={editTime}
                                    onChange={(e) => setEditTime(e.target.value)}
                                  />
                                </label>
                                <br />
                                <label>
                                  Location:
                                  <input
                                    type="text"
                                    value={editLocation}
                                    onChange={(e) => setEditLocation(e.target.value)}
                                  />
                                </label>
                                <br />
                                <button type="submit">Save</button>
                                <button type="button" onClick={() => setIsEditing(false)}>
                                  Cancel
                                </button>
                              </form>
                            </div>
                          )}
                        </div>
                      ))
                    ) : (
                      <li>{activities[trip.id]}</li>
                    )}
                  </ul>
                )}
              </li>
            ))}
          </ul>
        ) : (
          <p>No trips found. Create a new trip to get started!</p>
        )}
      </div>

      <div style={{ width: '30%', marginLeft: '20px' }}>
        <h3>Map</h3>
        <MapComponent width="100%" height="200px" />
      </div>
    </div>
  );
}

export default TripManager;
