import React, { useState } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '500px',
};

const center = {
  lat: 39.8283, // Default to the center of the USA
  lng: -98.5795,
};

const MapComponent = () => {
  const [mapCenter, setMapCenter] = useState(center);
  const [searchTerm, setSearchTerm] = useState('');

  // Function to handle location search and center the map
  const handleSearch = () => {
    fetch(`https://maps.googleapis.com/maps/api/geocode/json?address=${encodeURIComponent(searchTerm)}&key=YOUR_GOOGLE_MAPS_API_KEY`)
      .then(response => response.json())
      .then(data => {
        if (data.results && data.results[0]) {
          const location = data.results[0].geometry.location;
          setMapCenter({ lat: location.lat, lng: location.lng });
        } else {
          alert("Location not found");
        }
      })
      .catch(error => console.error("Error fetching location data:", error));
  };

  return (
    <div>
      <h2>Map Search</h2>
      <input
        type="text"
        placeholder="Enter a location"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>

      <LoadScript googleMapsApiKey="AIzaSyCbJ9OEK1bSd7DLg2XHOE8zT2PlBxODR1g">
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={mapCenter}
          zoom={5}
        >
          <Marker position={mapCenter} />
        </GoogleMap>
      </LoadScript>
    </div>
  );
};

export default MapComponent;
